    private void CheckinbuttonActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_CheckinbuttonActionPerformed
        
        String cin=cinin.getText();
        String phone=phonein.getText();
        String FN=fnin.getText();
        String nat=(String)natbox.getSelectedItem();
        String rn=(String)Roomnumberbox.getSelectedItem();
        String booked=bookedin.getText();
        Date currentDate = new Date();        
        SimpleDateFormat dateFormat = new SimpleDateFormat("dd/MM/yyyy");
        String cid=dateFormat.format(currentDate);
        String Query="insert into checkin(idC,cin,fullname,RN,bookedin,nationality,phonenumber,ci) values(incch.nextval,'"+cin+"','"+FN+"','"+rn+"','"+booked+"','"+nat+"','"+phone+"',to_date('"+cid+"','dd-mm-yyyy'))";
        Operations.DATA(Query,"Checked in Successfully");
        setVisible(false);
        new In().setVisible(true);
        
        String Query2="insert into client(idCl,cin,fullname,RN,nationality,phonenumber,ci,total) select idC,cin,fullname,RN,nationality,phonenumber,ci,price*bookedin from checkin,rooms where RN='"+rn+"' AND room_number='"+rn+"'";
        Operations.DATA(Query2,"client added Successfully");
//        String Querytwo="insert into client(idcl,cin,fullname,RN,nationality,phonennumber,ci,total) values(inccll,'"+cin+"','"+FN+"','"+rn+"','"+nat+"','"+phone+"','"+cid+"','"+total+"')";
//        Operations.DATA(Querytwo,"Checked in Successfully");
        
    }//GEN-LAST:event_CheckinbuttonActionPerformed

