    private void formComponentShown(java.awt.event.ComponentEvent evt) {//GEN-FIRST:event_formComponentShown
    ResultSet rs=Select.getData("Select * from Checkin Order by idC");
    DefaultTableModel model=(DefaultTableModel)chtable.getModel();
     try{ 
     while(rs.next())
     {
          model.addRow(new Object[]{rs.getString(1),rs.getString(2),rs.getString(3),rs.getString(4)});
     }
     rs.close();
     }
     catch(Exception e){JOptionPane.showMessageDialog(null,e);}
    }//GEN-LAST:event_formComponentShown

    private void choutbtActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_choutbtActionPerformed
       int row =chtable.getSelectedRow();
        String target=chtable.getModel().getValueAt(row,0).toString();
        Date currentDate = new Date();        
        SimpleDateFormat dateFormat = new SimpleDateFormat("dd/MM/yyyy");
        String cod=dateFormat.format(currentDate);
        String Querytwo="UPDATE client set co =(to_date('"+cod+"','dd-mm-yyyy')) where idCl =(select idc from checkin where idc ='"+target+"')";     
        Operations.DATA(Querytwo,"Client profile finished");
        String Query="DELETE FROM Checkin where idC="+target;
        Operations.DATA(Query,"Checked out Successfully");
        setVisible(false);
        new In().setVisible(true);
    }//GEN-LAST:event_choutbtActionPerformed

